jQuery(document).ready(function($){"use strict";var e=10,t=864e5,s=new Date,a=new Date(2017,5,12),d=new Date(s.getFullYear(),s.getMonth(),s.getDate()),n=Math.round((d-a)/864e5)%10+1,o=["января","февраля","марта","апреля","мая","июня","июля","августа","сентября","октября","ноября","декабря"];$(".day-"+n).addClass("active-day"),$(".day-"+(10==n?1:n+1)).addClass("next-day");for(var i=0;i<10;i++){var r=new Date(d),l=(n-1+i)%10+1;r.setDate(d.getDate()+i),$(".day-"+l+" .date").text(r.getDate()+" "+o[r.getMonth()])}$(".slider-ten-days").slick({slidesToShow:3,slidesToScroll:1,speed:300,cssEase:"ease-in-out",arrows:!0,focusOnSelect:!0,responsive:[{breakpoint:1024,settings:{slidesToShow:2,centerPadding:"50px"}},{breakpoint:600,settings:{slidesToShow:1,centerPadding:"50px"}},{breakpoint:480,settings:{slidesToShow:1,centerPadding:"0px"}}]}),$(".slider-ten-days").slick("slickGoTo",n-1)});